// Enhanced content extraction with WordPress-specific analysis
const webhookData = $('Webhook Trigger3').first().json.body;
const catalogData = $('Fetch Catalog Config').first().json;

// Handle potential website content fetch failure
const websiteContentNodes = $('Fetch Website Content3').all();
let websiteContent = null;
let html = '';

if (websiteContentNodes.length > 0 && websiteContentNodes[0].json) {
  websiteContent = websiteContentNodes[0].json;
  html = websiteContent.data || websiteContent.body || websiteContent.html || '';
} else {
  console.log('Warning: Website content fetch failed, proceeding with WordPress data only');
}

const extractEnhancedFeatures = (html, wordpressData) => {
  // Clean HTML
  let cleaned = html.replace(/<script[^>]*>.*?<\/script>/gis, '')
                   .replace(/<style[^>]*>.*?<\/style>/gis, '');

  // Extract basic info
  const titleMatch = cleaned.match(/<title[^>]*>([^<]*)<\/title>/i);
  const title = titleMatch ? titleMatch[1].trim() : '';

  const descMatch = cleaned.match(/<meta[^>]*name=[\"']description[\"'][^>]*content=[\"']([^\"']*)[\"']/i);
  const description = descMatch ? descMatch[1] : '';

  // Extract visible text content
  const bodyText = cleaned.replace(/<[^>]*>/g, ' ')
                          .replace(/\\s+/g, ' ')
                          .trim();

  // Extract headings
  const headings = [];
  const headingMatches = cleaned.match(/<h[1-6][^>]*>([^<]*)<\/h[1-6]>/gi) || [];
  headingMatches.forEach(match => {
    const text = match.replace(/<[^>]*>/g, '').trim();
    if (text.length > 2) headings.push(text);
  });

  // Enhanced WordPress-specific detection using actual WP data
  const wpPlugins = wordpressData?.plugins_info || {};
  const wpTheme = wordpressData?.theme_info || {};
  const contentAnalysis = wordpressData?.content_analysis || {};
  const fileStructure = wordpressData?.file_structure || {};

  const indicators = {
    // E-commerce detection
    hasWooCommerce: wpPlugins.hasOwnProperty('woocommerce') || /woocommerce|add.to.cart|shop|product|cart/i.test(html),
    hasShopify: /shopify/i.test(html),
    hasEcommerce: /shop|store|buy|purchase|cart|checkout|product/i.test(bodyText),

    // Page builders (from actual WordPress data)
    hasElementor: wpPlugins.hasOwnProperty('elementor') || /elementor/i.test(html),
    hasDivi: wpTheme?.name?.toLowerCase().includes('divi') || /divi/i.test(html),
    hasGutenberg: /wp-block-/i.test(html) && !/(hello.world|sample|default)/i.test(bodyText),
    hasVisualComposer: wpPlugins.hasOwnProperty('js_composer'),
    hasBeaver: wpPlugins.hasOwnProperty('bb-plugin'),

    // Content types
    hasBooking: wpPlugins.hasOwnProperty('booking') || contentAnalysis?.content_indicators?.includes('booking') || /booking|reservation|appointment/i.test(bodyText),
    hasEvents: wpPlugins.hasOwnProperty('the-events-calendar') || contentAnalysis?.content_indicators?.includes('events') || /event|calendar|schedule/i.test(bodyText),
    hasPortfolio: /portfolio|work|project|gallery|showcase/i.test(bodyText),
    hasBlog: /blog|post|article|news|read.more/i.test(bodyText),
    hasTestimonials: /testimonial|review|client|customer/i.test(bodyText),
    hasTeam: /team|staff|member|about.us|meet/i.test(bodyText),
    hasGallery: contentAnalysis?.content_indicators?.includes('gallery') || /gallery|photos/i.test(bodyText),

    // Functional features
    hasContactForm: wpPlugins.hasOwnProperty('contact-form-7') || contentAnalysis?.content_indicators?.includes('contact_forms') || /contact.form|get.in.touch/i.test(bodyText),
    hasNewsletter: wpPlugins.hasOwnProperty('mailchimp-for-wp') || contentAnalysis?.content_indicators?.includes('newsletter') || /newsletter|subscribe/i.test(bodyText),
    hasSocialMedia: /facebook|twitter|instagram|linkedin|social/i.test(html),
    hasSlider: contentAnalysis?.content_indicators?.includes('slider') || /slider|carousel/i.test(html),
    hasVideo: /video|youtube|vimeo/i.test(html),

    // Language/Accessibility
    hasMultilingual: wpPlugins.hasOwnProperty('sitepress-multilingual-cms') || wpPlugins.hasOwnProperty('polylang') || contentAnalysis?.content_indicators?.includes('multilingual'),

    // Business type indicators
    isRestaurant: /restaurant|cafe|bistro|menu|food|dining/i.test(bodyText),
    isRealEstate: /real.estate|property|house|apartment|rent|buy|mortgage/i.test(bodyText),
    isHealthcare: /health|medical|doctor|clinic|hospital|patient|therapy/i.test(bodyText),
    isLegal: /law|legal|lawyer|attorney|court|justice/i.test(bodyText),
    isEducation: /education|school|university|course|learn|student|teacher/i.test(bodyText),
    isAutomotive: /car|auto|vehicle|garage|repair|dealership/i.test(bodyText),
    isBeauty: /beauty|salon|spa|wellness|massage|hair|nail/i.test(bodyText),
    isConstruction: /construction|building|contractor|architect|engineering/i.test(bodyText),
    isFinance: /finance|bank|insurance|investment|loan|mortgage/i.test(bodyText),
    isTechnology: /technology|software|app|digital|IT|development|saas/i.test(bodyText),
    isCreative: /creative|design|art|photography|studio|agency/i.test(bodyText),
    isNonprofit: /nonprofit|charity|donation|volunteer|foundation/i.test(bodyText),
    isEvent: /event|conference|wedding|party|celebration/i.test(bodyText),
    isTravel: /travel|tourism|hotel|vacation|trip|destination/i.test(bodyText),

    // Theme and customization level
    isCustomized: fileStructure?.custom_themes > 0 || fileStructure?.has_custom_uploads,
    isMinimalContent: bodyText.length < 1000 || /hello.world|sample.page|coming.soon|default|lorem.ipsum/i.test(bodyText),
    hasChildTheme: fileStructure?.has_child_theme,

    // Plugin count indicators
    pluginCount: Object.keys(wpPlugins).length,
    hasMultiplePlugins: Object.keys(wpPlugins).length > 3,

    // Add fallback indicator when no HTML content available
    htmlContentAvailable: html.length > 0
  };

  return {
    title,
    description,
    bodyText: bodyText.substring(0, 3500), // More content for better analysis
    headings: headings.slice(0, 10),
    indicators,
    wordpressInfo: {
      version: wordpressData?.wp_info?.version,
      theme: wpTheme?.name || wordpressData?.theme_info?.active_theme,
      plugins: Object.keys(wpPlugins).slice(0, 10), // Top 10 plugins
      customization_level: fileStructure?.custom_themes > 0 ? 'high' : 'low',
      content_size: contentAnalysis?.uploads_size || 0
    }
  };
};

const templateData = {
  slug: webhookData.slug,
  name: webhookData.name,
  demo_url: webhookData.demo_url,
  screenshot_url: webhookData.screenshot_url,
  api_callback_url: webhookData.api_callback_url,
  api_token: webhookData.api_token
};

const content = extractEnhancedFeatures(html, webhookData.wordpress_data);
const availableCategories = catalogData.categories;
const availableTags = catalogData.tags;

return [{ templateData, content, availableCategories, availableTags }];

// Enhanced validation with WordPress data verification
const aiResponse = $input.first().json;
const templateData = $('Enhanced Content Analysis1').first().json.templateData;
const content = $('Enhanced Content Analysis1').first().json.content;
const availableTags = $('Enhanced Content Analysis1').first().json.availableTags;
const availableCategories = $('Enhanced Content Analysis1').first().json.availableCategories;

try {
  let classification;

  if (typeof aiResponse === 'string') {
    const cleanResponse = aiResponse.replace(/```json\n?/, '').replace(/```\n?$/, '').trim();
    classification = JSON.parse(cleanResponse);
  } else if (aiResponse && typeof aiResponse === 'object') {
    classification = aiResponse.output || aiResponse.text || aiResponse.response || aiResponse;

    if (typeof classification === 'string') {
      const cleanResponse = classification.replace(/```json\n?/, '').replace(/```\n?$/, '').trim();
      classification = JSON.parse(cleanResponse);
    }
  } else {
    throw new Error('Invalid AI response format');
  }

  // Validate structure
  if (!classification.primary_category || !Array.isArray(classification.tags)) {
    throw new Error('Invalid classification structure');
  }

  // Validate category
  if (!availableCategories.includes(classification.primary_category)) {
    console.log('Invalid category, defaulting to portfolio:', classification.primary_category);
    classification.primary_category = 'portfolio';
  }

  // Validate and filter tags
  classification.tags = classification.tags
    .filter(tag => availableTags.includes(tag))
    .slice(0, 5); // Max 5 tags

  // Enhanced WordPress-based validation
  const wpPlugins = Object.keys(content.wordpressInfo?.plugins || []);

  classification.tags = classification.tags.filter(tag => {
    if (tag === 'woocommerce' && !wpPlugins.includes('woocommerce') && !content.indicators.hasWooCommerce) {
      console.log('Removing woocommerce tag - not found in WordPress data');
      return false;
    }
    if (tag === 'elementor' && !wpPlugins.includes('elementor') && !content.indicators.hasElementor) {
      console.log('Removing elementor tag - not found in WordPress data');
      return false;
    }
    if (tag === 'divi' && !content.indicators.hasDivi) {
      console.log('Removing divi tag - not found in WordPress data');
      return false;
    }
    return true;
  });

  // Handle minimal content or no HTML content
  if (content.indicators.isMinimalContent || !content.indicators.htmlContentAvailable) {
    classification.tags = classification.tags.filter(tag => 
      ['responsive', 'contact_form', 'social_media'].includes(tag)
    ).slice(0, 2);

    if (classification.confidence > 0.4) {
      classification.confidence = Math.min(0.4, classification.confidence);
    }
  }

  // Validate confidence
  if (typeof classification.confidence !== 'number' || classification.confidence < 0 || classification.confidence > 1) {
    classification.confidence = 0.5;
  }

  // Ensure descriptions exist
  if (!classification.description_en) {
    classification.description_en = `${classification.primary_category.replace('_', ' ')} WordPress template`;
  }
  if (!classification.description_de) {
    classification.description_de = `${classification.primary_category.replace('_', ' ')} WordPress-Vorlage`;
  }

  // Add metadata about the analysis
  classification.metadata = {
    wordpress_version: content.wordpressInfo?.version,
    plugin_count: content.indicators?.pluginCount || 0,
    customization_level: content.wordpressInfo?.customization_level,
    analysis_source: 'enhanced_wordpress_data',
    html_content_available: content.indicators?.htmlContentAvailable || false
  };

  return [{
    json: {
      templateData,
      classification,
      success: true
    }
  }];

} catch (error) {
  // Conservative fallback
  return [{
    json: {
      templateData,
      classification: {
        primary_category: 'portfolio',
        tags: ['responsive'], // Only very basic tag
        confidence: 0.2,
        rationale: `Enhanced validation failed: ${error.message}. Using conservative fallback.`,
        description_en: 'WordPress template',
        description_de: 'WordPress-Vorlage',
        metadata: {
          analysis_source: 'fallback',
          error: error.message
        }
      },
      success: false,
      error: error.message
    }
  }];
}
